import { useEffect, useState } from "react";
import { v4 as uuidv4 } from "uuid";
import { appStyles } from "./styles";
import { Container } from "./components/Container";
import { Image } from "./components/Image";
import logo from "./assets/shopping-list.png";
import editIcon from "./assets/pencil.svg";
import saveIcon from "./assets/check.svg";
import { Font } from "./components/Font";
import { Input } from "./components/Input";
import { Button } from "./components/Button/Button";
import { generateRandomProducts, saveData } from "./utils";
import { T_ShoppingListItem } from "./types";

function App() {
  const [editProduct, setEditProduct] = useState<boolean>(false);

  useEffect(() => {
    const data: T_ShoppingListItem[] = generateRandomProducts();
    saveData(data);
  }, []);

  return (
    <Container styles={appStyles.appRootStyles}>
      <Container styles={appStyles.appBodyStyles}>
        <Container styles={appStyles.headerStyles}>
          <Image
            src={logo}
            alt="shopping-list-logo"
            styles={appStyles.logoStyles}
          />
          <Font styles={appStyles.appNameStyles}>Shopping List</Font>
        </Container>
        <hr style={{ width: "100%" }} />
        <Container styles={appStyles.bodyContainerStyles}>
          <Container styles={appStyles.shoppingListItemStyles}>
            {editProduct ? (
              <Image
                styles={appStyles.iconStyles}
                src={saveIcon}
                alt="check-icon"
              />
            ) : (
              <Image
                styles={appStyles.iconStyles}
                src={editIcon}
                alt="pencil-icon"
                onClick={() => setEditProduct(true)}
              />
            )}
            <Font
              styles={appStyles.shoppingListTextStyles({ type: "product" })}
            >
              Product 1
            </Font>
            <Input
              styles={appStyles.inputStyles({ type: "product" })}
              type="text"
              name="newName"
              value={""}
              onChange={() => {}}
            />
            <Font styles={appStyles.shoppingListTextStyles}>Amount</Font>
            <Input
              styles={appStyles.inputStyles}
              type="number"
              name="newAmount"
              value={""}
              onChange={() => {}}
            />
            <Button onClick={() => {}} styles={appStyles.buttonStyles}>
              remove
            </Button>
          </Container>
        </Container>

        <Font styles={appStyles.headingStyles}>Add a new product</Font>
        <hr style={{ width: "100%" }} />
        <Container styles={appStyles.bodyContainerStyles}>
          <Input
            styles={appStyles.inputStyles({ type: "product" })}
            type="text"
            name="productName"
            value={""}
            onChange={() => {}}
          />
          <Input
            styles={appStyles.inputStyles}
            type="number"
            name="amount"
            value={""}
            onChange={() => {}}
          />
          <Button onClick={() => {}} styles={{}}>
            MY BTN
          </Button>
        </Container>
      </Container>
    </Container>
  );
}

export default App;
