import { SystemStyleObject } from "@styled-system/css";
import backgroundImage from "../assets/background.jpeg";

export const appRootStyles = (): SystemStyleObject => {
  return {
    margin: 0,
    padding: 0,
    boxSizing: "border-box",
    backgroundImage: `url(${backgroundImage})`,
    backgroundColor: "#f2f2f2",
    backgroundSize: "cover",
    width: "100vw",
    height: "100vh",
    fontFamily: "lato, sans-serif",
  };
};
export const appBodyStyles = (): SystemStyleObject => {
  return {
    boxSizing: "border-box",
    width: "960px",
    margin: "0 auto",
  };
};

export const headerStyles = (): SystemStyleObject => {
  return {
    width: "100%",
    padding: "20px ",
    boxSizing: "border-box",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };
};

export const logoStyles = (): SystemStyleObject => {
  return {
    width: "80px",
    height: "80px",
  };
};

export const iconStyles = (): SystemStyleObject => {
  return {
    width: "10px",
    height: "10px",
  };
};

export const appNameStyles = (): SystemStyleObject => {
  return {
    fontSize: "80px",
    color: "#004975",
    width: "800px",
    margin: 0,
    textAlign: "center",
    fontWeight: 800,
  };
};

export const bodyContainerStyles = (): SystemStyleObject => {
  return {
    width: "100%",
    padding: "20px",
  };
};

export const headingStyles = (): SystemStyleObject => {
  return {
    fontSize: "22px",
    color: "#004975",
    margin: 0,
    fontWeight: 800,
  };
};

export const shoppingListItemStyles = (): SystemStyleObject => {
  return {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };
};
export const shoppingListTextStyles = (): SystemStyleObject => {
  return {
    fontSize: "16px",
    color: "#004975",
    margin: 0,
    fontWeight: 800,
  };
};
