import { useEffect, useState } from "react";
import { v4 as uuidv4 } from "uuid";
import { appStyles } from "./styles";
import { Container } from "./components/Container";
import { Image } from "./components/Image";
import logo from "./assets/shopping-list.png";
import editIcon from "./assets/pencil.svg";
import saveIcon from "./assets/check.svg";
import { Font } from "./components/Font";
import { Input } from "./components/Input";
import { Button } from "./components/Button/Button";
import { generateRandomProducts, saveData } from "./utils";
import { T_ShoppingListItem } from "./types";
import { useForm } from "./useForm";

function App() {
  const {
    newShoppingItem,
    setNewShoppingItem,
    shoppingItemChange,
    setShoppingItemChange,
  } = useForm();

  const [editProduct, setEditProduct] = useState<string>("");
  const [data, setData] = useState<T_ShoppingListItem[]>([]);
  const [isItemReady, setIsItemReady] = useState<boolean>(false);

  useEffect(() => {
    const randomData: T_ShoppingListItem[] = generateRandomProducts();
    setData(randomData);
  }, []);

  useEffect(() => {
    newShoppingItem.amount && newShoppingItem
      ? setIsItemReady(true)
      : setIsItemReady(false);
  }, [newShoppingItem]);

  const handleChangeUpdateProduct = (e: EventTarget & HTMLInputElement) => {
    setShoppingItemChange({ ...shoppingItemChange, [e.name]: e.value });
  };
  const handleChangeNewProduct = (e: EventTarget & HTMLInputElement) => {
    setNewShoppingItem({ ...newShoppingItem, [e.name]: e.value });
  };

  const handleAddNewShoppingItem = (newProduct: T_ShoppingListItem) => {
    const newItem = {
      id: newProduct.id,
      name: newProduct.name,
      amount: newProduct.amount,
    };
    setData((prevState) => prevState.concat(newItem));
    setNewShoppingItem({ name: "", amount: "" });
  };

  const handleRemoveItemFromList = (id: string) => {
    const filteredData = data.filter((item) => item.id !== id);
    setData(filteredData);
  };

  const handleUpdateItem = (updateData: T_ShoppingListItem) => {
    const updatedData = data.map((item) => {
      if (item.id === updateData.id) {
        const name = updateData.name ? updateData.name : item.name;
        const amount = updateData.amount ? updateData.amount : item.amount;
        return {
          ...item,
          name: name,
          amount: amount,
        };
      }
      return item;
    });
    setData(updatedData);
  };
  // console.log("test the test--", shoppingItemChange.newName);

  return (
    <Container styles={appStyles.appRootStyles}>
      <Container styles={appStyles.appBodyStyles}>
        <Container styles={appStyles.headerStyles}>
          <Image
            src={logo}
            alt="shopping-list-logo"
            styles={appStyles.logoStyles}
          />
          <Font styles={appStyles.appNameStyles}>Shopping List</Font>
        </Container>
        <Font
          styles={appStyles.headingStyles({ align: "center" })}
        >{`Shopping List `}</Font>
        <hr style={{ width: "100%" }} />
        <Container styles={appStyles.bodyContainerStyles}>
          {data.map((item: T_ShoppingListItem) => (
            <Container key={item.id} styles={appStyles.shoppingListItemStyles}>
              {item.id === editProduct ? (
                <Image
                  onClick={() => {
                    handleUpdateItem({
                      id: item.id,
                      name: shoppingItemChange.newName,
                      amount: shoppingItemChange.newAmount,
                    });
                    setEditProduct("");
                  }}
                  styles={appStyles.iconStyles}
                  src={saveIcon}
                  alt="check-icon"
                />
              ) : (
                <Image
                  styles={appStyles.iconStyles}
                  src={editIcon}
                  alt="pencil-icon"
                  onClick={() => setEditProduct(item.id)}
                />
              )}
              {item.id === editProduct ? (
                <Input
                  styles={appStyles.inputStyles({ type: "product" })}
                  type="text"
                  name="newName"
                  placeholder="Product Name"
                  defaultValue={item.name}
                  required={true}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
                    handleChangeUpdateProduct(e.currentTarget)
                  }
                />
              ) : (
                <Font
                  styles={appStyles.shoppingListTextStyles({ type: "product" })}
                >
                  {item.name}
                </Font>
              )}
              {item.id === editProduct ? (
                <Input
                  styles={appStyles.inputStyles}
                  type="number"
                  name="newAmount"
                  placeholder="Amount"
                  min={1}
                  defaultValue={item.amount}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
                    handleChangeUpdateProduct(e.currentTarget)
                  }
                />
              ) : (
                <Font styles={appStyles.shoppingListTextStyles}>
                  {item.amount}
                </Font>
              )}
              <Button
                onClick={() => handleRemoveItemFromList(item.id)}
                styles={appStyles.buttonStyles}
              >
                remove
              </Button>
            </Container>
          ))}
        </Container>

        <Font styles={appStyles.headingStyles}>Add a new product</Font>
        <hr style={{ width: "100%" }} />
        <Container styles={appStyles.bodyContainerStyles}>
          <form>
            <Container styles={appStyles.addNewItemContainerStyles}>
              <Input
                styles={appStyles.inputStyles({ type: "product" })}
                type="text"
                name="name"
                placeholder="Product Name"
                value={newShoppingItem.name}
                required={true}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleChangeNewProduct(e.currentTarget)
                }
              />
              <Input
                styles={appStyles.inputStyles}
                type="number"
                name="amount"
                placeholder="Amount"
                min={1}
                value={newShoppingItem.amount}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleChangeNewProduct(e.currentTarget)
                }
              />
              <Button
                onClick={() =>
                  handleAddNewShoppingItem({
                    id: uuidv4(),
                    name: newShoppingItem.name,
                    amount: newShoppingItem.amount,
                  })
                }
                styles={appStyles.buttonStyles}
              >
                Add
              </Button>
            </Container>
          </form>
        </Container>
        <hr style={{ width: "100%", marginBottom: "100px" }} />
        <Font styles={appStyles.appTextStyles}>
          © Chiranjibi Chapagain 2024
        </Font>
      </Container>
    </Container>
  );
}

export default App;
