import { useState } from "react";

type T_List = {
  name: string;
  amount: number;
};
type T_UpdateList = {
  newName: string;
  newAmount: number;
};

export const useForm = () => {
  const [newShoppingItem, setNewShoppingItem] = useState<T_List>({
    name: "",
    amount: 0,
  });

  const [shoppingItemChange, setShoppingItemChange] = useState<T_UpdateList>({
    newName: "",
    newAmount: 0,
  });

  return {
    newShoppingItem,
    setNewShoppingItem,
    shoppingItemChange,
    setShoppingItemChange,
  };
};
